import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const User = () => {
  const [user, setUser] = useState({
    nome_produto: "",
    descricao: "",
    preco_produto: "",
    quantidade_produto: "",
    img_produto: "",
  });

  const { id } = useParams();
  useEffect(() => {
    loadUser();
  }, [loadUser]);

  const loadUser = async () => {
    const res = await axios.get(
      `http://localhost:3333/api/produtos.details/${id}`
    );
    setUser(res.data);
    console.log(res.data);
  };

  return (
    <div className='container py-4'>
      <Link className='btn btn-primary' to='/'>
        back to Home
      </Link>
      <h1 className='display-4'>User Id: {id}</h1>
      <hr />
      <ul className='list-group w-50'>
        <li className='list-group-item'>name: {user.nome_produto}</li>
        <li className='list-group-item'>name: {user.descricao}</li>
        <li className='list-group-item'>name: {user.preco_produto}</li>
        <li className='list-group-item'>name: {user.quantidade_produto}</li>
        <li className='list-group-item'>name: {user.img_produto}</li>
      </ul>
    </div>
  );
};

export default User;
