import React from "react";
import "./Card.css";

const PromotionCard = ({ bla }) => (
  <div className='promotion-card'>
    <img
      src={bla.img_produto}
      className='promotion-card__image'
      alt={bla.title}
    />
    <div className='promotion-card__info'>
      <span className='promotion-card__price'>Produto: {bla.nome_produto}</span>
      <span className='promotion-card__price'>
        Descricao Produto: {bla.descricao}
      </span>
      <span className='promotion-card__price'>Pre√ßo {bla.preco_produto}</span>
      <span className='promotion-card__price'>
        Quantiodade {bla.quantidade_produto}
      </span>
    </div>
  </div>
);

export default PromotionCard;
