import React from "react";
import "./Card.css";

// "nome_produto":"Produto 03",
// "descricao":"bla",
// "preco_produto":"100",
// "quantidade_produto":"50"

const PromotionCard = ({ promotion }) => (
  <div className='promotion-card'>
    <img
      src={promotion.image}
      className='promotion-card__image'
      alt={promotion.title}
    />
    <div className='promotion-card__info'>
      <span className='promotion-card__price'> Produto: {promotion.name} </span>
      <span className='promotion-card__price'>
        {" "}
        Descricao Produto: {promotion.descricao}
      </span>
      <span className='promotion-card__price'>
        {" "}
        Pre√ßo {promotion.preco_produto}
      </span>
      <span className='promotion-card__price'>
        {" "}
        Quantiodade {promotion.quantidade_produto}
      </span>

      <footer className='promotion-card__footer'>
        <a
          href={promotion.url}
          target='_blank'
          rel='noopener noreferrer'
          className='promotion-card__link'
        >
          IR PARA O SITE
        </a>
      </footer>
    </div>
  </div>
);

export default PromotionCard;
