import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";

const initialValue = {
  title: "",
  owner: "",
  idade: "",
  img: "",
  name: "",
};

const Formulario = () => {
  const [values, setValues] = useState(initialValue);
  const history = useHistory();

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    ev.preventDefault();

    axios.post("http://localhost:3333/projects/", values).then(() => {
      history.push("/");
    });
  }

  return (
    <div>
      <h1>Promo Show</h1>
      <h2>Nova Promoção</h2>

      <form onSubmit={onSubmit}>
        <div className='promotion-form__group'>
          <label htmlFor='title'>Título</label>
          <input id='title' name='title' type='text' onChange={onChange} />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='owner'>owner</label>
          <input id='owner' name='owner' type='text' onChange={onChange} />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='name'>name</label>
          <input id='name' name='name' type='text' onChange={onChange} />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='idade'>idade</label>
          <input id='idade' name='owner' type='number' onChange={onChange} />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='img'>Imagem (URL)</label>
          <input
            id='imageUrl'
            name='imageUrl'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='price'>Preço</label>
          <input id='price' name='price' type='number' onChange={onChange} />
        </div>
        <div>
          <button type='submit'>Salvar</button>
        </div>
      </form>
    </div>
  );
};

export default Formulario;
