import React, { useEffect, useState } from "react";

import axios from "axios";
import { Link } from "react-router-dom";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Card from "@material-ui/core/Card";

const Home = () => {
  const [promotions, setPromotions] = useState([]);
  const [busca, setBusca] = useState("");

  useEffect(() => {
    const params = {};

    if (busca) {
      params.id = busca;
    }

    loadUsers();
  }, [busca]);

  const loadUsers = async () => {
    const result = await axios.get("http://localhost:3333/api/produtos");
    setPromotions(result.data);
  };

  const deleteUser = async (_id) => {
    await axios.delete(`http://localhost:3333/api/delete/produtos/${_id}`);
    loadUsers();
  };

  return (
    <div className='container'>
      <div className='py-4'>
        <Link to='/PageForm'>
          <h1>Novo usuario</h1>
        </Link>
        <input
          type='search'
          placeholder='buscar'
          value={busca}
          onChange={(ev) => setBusca(ev.target.value)}
        />

        <table class='table border shadow'>
          <tbody>
            {promotions.map((user) => (
              <tr key={user._id}>
                <td>{user.nome_produto}</td>
                <td>{user.descricao}</td>
                <td>{user.quantidade_produto}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Card>
        //{" "}
        <TableContainer>
          //{" "}
          <Table aria-label='simple table'>
            //{" "}
            <TableHead>
              //{" "}
              <TableRow>
                // <TableCell align='right'>Carbs&nbsp;(g)</TableCell>
                // <TableCell align='right'>Protein&nbsp;(g)</TableCell>
                //{" "}
              </TableRow>
              //{" "}
            </TableHead>
            //{" "}
            <TableBody>
              //{" "}
              <TableRow>
                // <TableCell align='right'>dsadasdas</TableCell>
                // <TableCell align='right'>dsadadas</TableCell>
                //{" "}
                {promotions.map((user) => (
                  <>
                    <td>{user.nome_produto}</td>
                    <td>{user.descricao}</td>
                    <td>{user.quantidade_produto}</td>
                  </>
                ))}
              </TableRow>
            </TableBody>
          </Table>
        </TableContainer>
      </Card>
    </div>
  );
};

export default Home;

