import React, { useEffect, useState } from "react";

import axios from "axios";
import { Link } from "react-router-dom";
import Button from "@material-ui/core/Button";
import "./index.css";
const Home = () => {
  const [promotions, setPromotions] = useState([]);
  const [busca, setBusca] = useState("");

  useEffect(() => {
    const params = {};

    if (busca) {
      params.id = busca;
    }

    loadUsers();
  }, [busca]);

  const loadUsers = async () => {
    const result = await axios.get("http://localhost:3333/api/produtos");
    setPromotions(result.data);
  };

  const deleteUser = async (_id) => {
    await axios.delete(`http://localhost:3333/api/delete/produtos/${_id}`);
    loadUsers();
  };

  return (
    <div>
      <Link to='/PageForm'>
        <h1>Novo usuario</h1>
      </Link>
      <input
        type='search'
        placeholder='Buscar'
        value={busca}
        onChange={(ev) => setBusca(ev.target.value)}
      />

      <table class='table table-responsive'>
        <thead>
          <tr>
            <th>Nome</th>
            <th>Descrição</th>
            <th>Preço</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {promotions.map((user) => (
            <tr key={user._id}>
              <td>{user.nome_produto}</td>
              <td>{user.descricao}</td>
              <td>{user.quantidade_produto}</td>
              <td>
                <Link class='btn btn-primary mr-2' to={`/users/${user._id}`}>
                  Vizualizar
                </Link>
                <br></br>
                <Link
                  class='btn btn-outline-primary mr-2'
                  to={`/users/edit/${user._id}`}
                >
                  Editar
                </Link>
                <br></br>
                <Link
                  class='btn btn-danger'
                  onClick={() => deleteUser(user._id)}
                >
                  Delete
                </Link>

                <Button variant='contained' color='secondary'>
                  Secondary
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>

    // <div className='container'>
    //   <div className='py-4'>
    //     <Link to='/PageForm'>
    //       <h1>Novo usuario</h1>
    //     </Link>
    //     <input
    //       type='search'
    //       placeholder='buscar'
    //       value={busca}
    //       onChange={(ev) => setBusca(ev.target.value)}
    //     />

    //     <table class='table border shadow'>
    // <tbody>
    //   {promotions.map((user) => (
    //     <tr key={user._id}>
    //       <td>{user.nome_produto}</td>
    //       <td>{user.descricao}</td>
    //       <td>{user.quantidade_produto}</td>
    //       <td>
    //         <Link class='btn btn-primary mr-2' to={`/users/${user._id}`}>
    //           View
    //         </Link>

    //         <Link
    //           class='btn btn-outline-primary mr-2'
    //           to={`/users/edit/${user._id}`}
    //         >
    //           Edit
    //         </Link>

    //         <Link
    //           class='btn btn-danger'
    //           onClick={() => deleteUser(user._id)}
    //         >
    //           Delete
    //         </Link>
    //       </td>
    //     </tr>
    //   ))}
    // </tbody>
    //     </table>
    //   </div>
    // </div>
  );
};

export default Home;
