import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";
import "./edit.css";
const EditUser = () => {
  let history = useHistory();
  const { id } = useParams();
  const [user, setUser] = useState({});

  const {
    nome_produto,
    descricao,
    preco_produto,
    quantidade_produto,
    img_produto,
  } = user;
  const onInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    loadUser();
  });

  const loadUser = async () => {
    const result = await axios.get(`http://localhost:3333/api/produtos/${id}`);
    setUser(result.data);
    console.log(result.data);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:3333/api/produtos/${id}`, user);
    history.push("/");
  };

  return (
    <div className='container' id='page-teacher-form'>
      <main>
        <form onSubmit={handleCreateClass}>
          <fieldset>
            <legend>Seus Dados</legend>
          </fieldset>

          <fieldset>
            <legend>Sobre a aula</legend>
          </fieldset>

          <fieldset>
            <legend>
              Horários Disponiveis
              <button onClick={addNewScheduleItem} type='button'>
                + Novo Horario
              </button>
            </legend>

            {scheduleItems.map((schedule, index) => {
              return (
                <div key={schedule.week_day} className='schedule-item'>
                  <Select
                    name='week_day'
                    label='Dia da Semana'
                    value={schedule.week_day}
                    onChange={(e) =>
                      setscheduleItemValue(index, "week_day", e.target.value)
                    }
                    options={[
                      { value: "0", label: "Domingo" },
                      { value: "1", label: "Segunda" },
                      { value: "2", label: "Terça" },
                      { value: "3", label: "Quarta" },
                      { value: "4", label: "Quinta" },
                      { value: "5", label: "Sexta" },
                      { value: "6", label: "Sábado" },
                    ]}
                  />
                  <Input
                    name='from'
                    label='Das'
                    type='time'
                    value={schedule.from}
                    onChange={(e) =>
                      setscheduleItemValue(index, "from", e.target.value)
                    }
                  />
                  <Input
                    name='to'
                    label='Até'
                    type='time'
                    value={schedule.to}
                    onChange={(e) =>
                      setscheduleItemValue(index, "to", e.target.value)
                    }
                  />
                </div>
              );
            })}
          </fieldset>

          <footer>
            <p>
              <img src={warningIcon} alt='Aviso Importante' />
              Importante <br />
              Preencha todos os dados
            </p>
            <button type='submit'>Salvar cadastro</button>
          </footer>
        </form>
      </main>
    </div>

    // <div className='container'>
    //   <div className='w-75 mx-auto shadow p-5'>
    //     <h2 className='text-center mb-4'>Editar Usuário</h2>
    //     <form onSubmit={(e) => onSubmit(e)}>
    //       <div className='form-group'>
    //         <input
    //           type='text'
    //           className='form-control form-control-lg'
    //           placeholder='Novo Nome do Produto'
    //           name='nome_produto'
    //           value={nome_produto}
    //           onChange={(e) => onInputChange(e)}
    //         />
    //       </div>
    //       <div className='form-group'>
    //         <input
    //           type='text'
    //           className='form-control form-control-lg'
    //           placeholder='Nova descrição'
    //           name='descricao'
    //           value={descricao}
    //           onChange={(e) => onInputChange(e)}
    //         />
    //       </div>
    //       <div className='form-group'>
    //         <input
    //           type='text'
    //           className='form-control form-control-lg'
    //           placeholder='Preço produto'
    //           name='preco_produto'
    //           value={preco_produto}
    //           onChange={(e) => onInputChange(e)}
    //         />
    //       </div>
    //       <div className='form-group'>
    //         <input
    //           type='text'
    //           className='form-control form-control-lg'
    //           placeholder='Quantidade'
    //           name='quantidade_produto'
    //           value={quantidade_produto}
    //           onChange={(e) => onInputChange(e)}
    //         />
    //       </div>

    //       <div className='form-group'>
    //         <input
    //           type='text'
    //           className='form-control form-control-lg'
    //           placeholder='Imagem do Produto'
    //           name='img_produto'
    //           value={img_produto}
    //           onChange={(e) => onInputChange(e)}
    //         />
    //       </div>

    //       <button className='btn btn-warning btn-block'>Update User</button>
    //     </form>
    //   </div>
    // </div>
  );
  <div className='container' id='page-teacher-form'>
    <PageHeader
      title='Que incrível que você quer dar aulas'
      description='O primeiro passo é preencher esse formulario de inscrição.'
    />

    <main>
      <form onSubmit={handleCreateClass}>
        <fieldset>
          <legend>Seus Dados</legend>
          <Input
            name='name'
            label='Nome Completo'
            value={name}
            onChange={(e) => {
              setName(e.target.value);
            }}
          />
          <Input
            name='avatar'
            label='Avatar'
            value={avatar}
            onChange={(e) => {
              setAvatar(e.target.value);
            }}
          />
          <Input
            name='whatsapp'
            label='Whatsapp'
            value={whatsapp}
            onChange={(e) => {
              setWhatsapp(e.target.value);
            }}
          />
          <Textarea
            name='bio'
            label='Biografia'
            value={bio}
            onChange={(e) => {
              setBio(e.target.value);
            }}
          />
        </fieldset>

        <fieldset>
          <legend>Sobre a aula</legend>
          <Select
            name='subject'
            label='Matéria'
            value={subject}
            onChange={(e) => {
              setSubject(e.target.value);
            }}
            options={[
              { value: "Artes", label: "Artes" },
              { value: "Biologia", label: "Biologia" },
              { value: "Geografia", label: "Geografia" },
              { value: "Matematica", label: "Matematica" },
              { value: "Ingles", label: "Ingles" },
              { value: "Historia", label: "Historia" },
              { value: "Fisica", label: "Fisica" },
              { value: "Portugues", label: "Portugues" },
              { value: "Quimica", label: "Quimica" },
            ]}
          />
          <Input
            name='cost'
            label='Custo da sua hora por aula'
            value={cost}
            onChange={(e) => {
              setCost(e.target.value);
            }}
          />
        </fieldset>

        <fieldset>
          <legend>
            Horários Disponiveis
            <button onClick={addNewScheduleItem} type='button'>
              + Novo Horario
            </button>
          </legend>

          {scheduleItems.map((schedule, index) => {
            return (
              <div key={schedule.week_day} className='schedule-item'>
                <Select
                  name='week_day'
                  label='Dia da Semana'
                  value={schedule.week_day}
                  onChange={(e) =>
                    setscheduleItemValue(index, "week_day", e.target.value)
                  }
                  options={[
                    { value: "0", label: "Domingo" },
                    { value: "1", label: "Segunda" },
                    { value: "2", label: "Terça" },
                    { value: "3", label: "Quarta" },
                    { value: "4", label: "Quinta" },
                    { value: "5", label: "Sexta" },
                    { value: "6", label: "Sábado" },
                  ]}
                />
                <Input
                  name='from'
                  label='Das'
                  type='time'
                  value={schedule.from}
                  onChange={(e) =>
                    setscheduleItemValue(index, "from", e.target.value)
                  }
                />
                <Input
                  name='to'
                  label='Até'
                  type='time'
                  value={schedule.to}
                  onChange={(e) =>
                    setscheduleItemValue(index, "to", e.target.value)
                  }
                />
              </div>
            );
          })}
        </fieldset>

        <footer>
          <p>
            <img src={warningIcon} alt='Aviso Importante' />
            Importante <br />
            Preencha todos os dados
          </p>
          <button type='submit'>Salvar cadastro</button>
        </footer>
      </form>
    </main>
  </div>;
};

export default EditUser;
