import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import axios from "axios";
import * as Yup from "yup";
import { Form, Input } from "@rocketseat/unform";

const schema = Yup.object().shape({
  nome_produto: Yup.string().required("Nome do Produto obrigatorio"),
  descricao: Yup.string().required("Descrição Obrigatoria"),
  preco_produto: Yup.string().required("Preço é obrigatorio"),
  quantidade_produto: Yup.string().required("Quantidade obrigatoria"),
  img_produto: Yup.string().required("A Imagem é obrigatoria"),
});

const Formulario = () => {
  const [values, setValues] = useState("");
  const history = useHistory();

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    axios.post("http://localhost:3333/produtos", values).then(() => {
      history.push("/");
    });
  }

  return (
    <div>
      <h1>Produto Show </h1>
      <h2>Novo Produto</h2>

      <Form schema={schema} onSubmit={onSubmit}>
        <div className='promotion-form__group'>
          <label htmlFor='nome_produto'>Nome do Produto</label>
          <Input
            id='nome_produto'
            name='nome_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='descricao'>Descrição do Produto</label>
          <Input
            id='descricao'
            name='descricao'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='preco_produto'>Preço do Produto</label>
          <Input
            id='preco_produto'
            name='preco_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='quantidade_produto'>Quantidade do Produto</label>
          <Input
            id='quantidade_produto'
            name='quantidade_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='img_produto'>Selecione Imagem do Produto</label>
          <Input
            id='img_produto'
            name='img_produto'
            type='text'
            onChange={onChange}
          />
        </div>

        <div>
          <button type='submit'>Salvar</button>
        </div>

        <div>
          <Link to={"/"}>Voltar </Link>
        </div>
      </Form>
    </div>

    // <div class='form-wrapper'>
    //   <h1>Produto Show </h1>
    //   <h2>Novo Produto</h2>
