import React, { useEffect, useState } from "react";

import LoadingList from "components/Loading";
import axios from "axios";
import { Link } from "react-router-dom";

const Home = () => {
  const [promotions, setPromotions] = useState([]);
  const [busca, setBusca] = useState("");

  useEffect(() => {
    const params = {};
    if (busca) {
      params.id = busca;
    }

    axios
      .get("http://localhost:3333/api/produtos", { params })
      .then((response) => {
        setPromotions(response.data);
        console.log(response.data);
      });
  }, [busca]);

  const deleteUser = async (_id) => {
    await axios.delete(`http://localhost:3333/api/delete/produtos/${_id}`);
  };

  return (
    <div className='container'>
      <div className='py-4'>
        <h1>Home Page</h1>
        <table class='table border shadow'>
          <tbody>
            {promotions.map((user) => (
              <tr>
                <td>{user.nome_produto}</td>
                <td>{user.descricao}</td>
                <td>{user.quantidade_produto}</td>
                <td>
                  <Link
                    class='btn btn-primary mr-2'
                    to={`/promotions/${user._id}`}
                  >
                    View
                  </Link>
                  <Link
                    class='btn btn-outline-primary mr-2'
                    to={`/promotions/edit/${user._id}`}
                  >
                    Edit
                  </Link>
                  <Link
                    class='btn btn-danger'
                    onClick={() => deleteUser(user._id)}
                  >
                    Delete
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Home;
