import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import * as Yup from "yup";
import { Form, Input } from "@rocketseat/unform";

const schema = Yup.object().shape({
  nome_produto: Yup.string()
    .email("insira uma email valido")
    .required("email obrigatio"),
  descricao: Yup.string().required("A senha é obrigatoria"),
  preco_produto: Yup.string().required("A senha é obrigatoria"),
  quantidade_produto: Yup.string().required("A senha é obrigatoria"),
  img_produto: Yup.string().required("A senha é obrigatoria"),
});

const initialValue = {
  nome_produto: " ",
  descricao: "",
  preco_produto: "",
  quantidade_produto: "",
  img_produto: "",
};

const Formulario = () => {
  const [values, setValues] = useState(initialValue);
  const history = useHistory();

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    ev.preventDefault();

    axios.post("http://localhost:3333/produtos", values).then(() => {
      history.push("/");
    });
  }

  return (
    <div>
      <h1>Produto Show </h1>
      <h2>Novo Produto</h2>

      <Form schema={schema} onSubmit={onSubmit}>
        <div className='promotion-form__group'>
          <label htmlFor='nome_produto'>Nome do Produto</label>
          <Input
            id='nome_produto'
            name='nome_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='descricao'>Descrição do Produto</label>
          <Input
            id='descricao'
            name='descricao'
            type='text'
            onChange={onChange}
          />
        </div>

        <div className='promotion-form__group'>
          <label htmlFor='preco_produto'>Preço do Produto</label>
          <Input
            id='preco_produto'
            name='preco_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='quantidade_produto'>Quantidade do Produto</label>
          <Input
            id='quantidade_produto'
            name='quantidade_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='img_produto'>Selecione Imagem do Produto</label>
          <Input
            id='img_produto'
            name='img_produto'
            type='text'
            onChange={onChange}
          />
        </div>

        <div>
          <button type='submit'>Salvar</button>
        </div>
      </Form>
    </div>
  );
};

export default Formulario;
