import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";
import "./edit.css";
const EditUser = () => {
  let history = useHistory();
  const { id } = useParams();
  const [user, setUser] = useState({});

  const {
    nome_produto,
    descricao,
    preco_produto,
    quantidade_produto,
    img_produto,
  } = user;
  const onInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    loadUser();
  }, []);

  const loadUser = async () => {
    const result = await axios.get(`http://localhost:3333/api/produtos/${id}`);
    setUser(result.data);
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:3333/api/produtos/${id}`, user);
    history.push("/");
  };

  return (
    <div class='form-wrapper'>
      <h1>Editar Produto</h1>
      <form onSubmit={(e) => onSubmit(e)}>
        <div class='input-text'>
          <label for='label2'>Nome do Produto</label>
          <input
            type='text'
            className='form-control form-control-lg'
            name='nome_produto'
            value={nome_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div class='input-text'>
          <label for='label2'>Descrição</label>
          <input
            type='text'
            className='form-control form-control-lg'
            name='descricao'
            value={descricao}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div class='input-text'>
          <label for='label2'>Preço</label>
          <input
            type='text'
            className='form-control form-control-lg'
            name='preco_produto'
            value={preco_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div class='input-text'>
          <label for='label2'>Quantidade</label>
          <input
            type='text'
            className='form-control form-control-lg'
            name='quantidade_produto'
            value={quantidade_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div class='input-text'>
          <label for='label2'>Imagem Do Produto</label>
          <input
            type='text'
            className='form-control form-control-lg'
            name='img_produto'
            value={img_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <button className='btn btn-warning btn-block'>Update User</button>
      </form>
    </div>
  );
};

export default EditUser;
