import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import * as yup from "yup";

const validationSchema = yup.object().shape({
  name: yup.string().required(),
});
const initialValue = {
  nome_produto: " ",
  descricao: "",
  preco_produto: "",
  quantidade_produto: "",
  img_produto: "",
};

const Formulario = () => {
  const [values, setValues] = useState(initialValue);
  const history = useHistory();

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({ ...values, [name]: value });
  }

  function onSubmit(ev) {
    ev.preventDefault();

    axios.post("http://localhost:3333/produtos", values).then(() => {
      history.push("/");
    });
  }

  return (
    <div>
      <h1>Produto Show </h1>
      <h2>Novo Produto</h2>

      <form onSubmit={onSubmit}>
        <div className='promotion-form__group'>
          <label htmlFor='nome_produto'>Nome do Produto</label>
          <input
            id='nome_produto'
            name='nome_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='descricao'>Descrição do Produto</label>
          <input
            id='descricao'
            name='descricao'
            type='text'
            onChange={onChange}
          />
        </div>

        <div className='promotion-form__group'>
          <label htmlFor='preco_produto'>Preço do Produto</label>
          <input
            id='preco_produto'
            name='preco_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='quantidade_produto'>Quantidade do Produto</label>
          <input
            id='quantidade_produto'
            name='quantidade_produto'
            type='text'
            onChange={onChange}
          />
        </div>
        <div className='promotion-form__group'>
          <label htmlFor='img_produto'>Selecione Imagem do Produto</label>
          <input
            id='img_produto'
            name='img_produto'
            type='text'
            onChange={onChange}
          />
        </div>

        <div>
          <button type='submit'>Salvar</button>
        </div>
      </form>
    </div>
  );
};

export default Formulario;
