import React, { useEffect, useState } from "react";

import LoadingList from "components/Loading";
import axios from "axios";

const Home = () => {
  const [promotions, setPromotions] = useState([]);
  const [busca, setBusca] = useState("");

  useEffect(() => {
    const params = {};

    if (busca) {
      params.title_like = busca;
    }

    axios
      .get("http://localhost:5000/promotions", { params })
      .then((response) => {
        setPromotions(response.data);
      });
  }, [busca]);
  return (
    <div
      className='App'
      style={{
        maxWidth: 800,
        margin: "30px auto",
      }}
    >
      <input
        type='search'
        placeholder='buscar'
        value={busca}
        onChange={(ev) => setBusca(ev.target.value)}
      />
      <LoadingList promotions={promotions} loading={!promotions.length} />
    </div>
  );
};

export default Home;
