import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory, useParams } from "react-router-dom";

const EditUser = () => {
  let history = useHistory();
  const { id } = useParams();
  const [user, setUser] = useState({
    nome_produto: "",
    descricao: "",
    preco_produto: "",
    quantidade_produto: "",
    img_produto: "",
  });

  const {
    nome_produto,
    descricao,
    preco_produto,
    quantidade_produto,
    img_produto,
  } = user;
  const onInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    loadUser();
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:3333/api/produtos/${id}`, user);
    history.push("/");
  };

  const loadUser = async () => {
    const result = await axios.get(`http://localhost:3333/api/produtos/${id}`);
    setUser(result.data);
  };
  return (
    <div className='container'>
      <div className='w-75 mx-auto shadow p-5'>
        <h2 className='text-center mb-4'>Editar Usu√°rio</h2>

        <div className='form-group'>
          <input
            type='text'
            className='form-control form-control-lg'
            placeholder='Novo Nome do Produto'
            name='nome_produto'
            value={nome_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            className='form-control form-control-lg'
            placeholder='Nova descri√ß√£o'
            name='descricao'
            value={descricao}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            className='form-control form-control-lg'
            placeholder='Pre√ßo produto'
            name='preco_produto'
            value={preco_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            className='form-control form-control-lg'
            placeholder='Quantidade'
            name='quantidade_produto'
            value={quantidade_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>

        <div className='form-group'>
          <input
            type='text'
            className='form-control form-control-lg'
            placeholder='Imagem do Produto'
            name='img_produto'
            value={img_produto}
            onChange={(e) => onInputChange(e)}
          />
        </div>

        <button className='btn btn-warning btn-block'>Update User</button>
      </div>
    </div>
  );
};

export default EditUser;
