import React from "react";
import "./Card.css";

const PromotionCard = ({ promotion }) => (
  <div className='promotion-card'>
    <img
      src={promotion.img_produto}
      className='promotion-card__image'
      alt={promotion.title}
    />
    <div className='promotion-card__info'>
      <span className='promotion-card__price'>
        Produto: {promotion.nome_produto}
      </span>
      <span className='promotion-card__price'>
        Descricao Produto: {promotion.descricao}
      </span>
      <span className='promotion-card__price'>
        Pre√ßo {promotion.preco_produto}
      </span>
      <span className='promotion-card__price'>
        Quantiodade {promotion.quantidade_produto}
      </span>
    </div>

    <Link class='btn btn-danger' onClick={() => deleteUser(user._id)}>
      Delete
    </Link>
  </div>
);

export default PromotionCard;
