import React, { useEffect, useState } from "react";
import "./index.css";
import axios from "axios";
import { Link } from "react-router-dom";
import Card from "@material-ui/core/Card";
import Table from "@material-ui/core/Table";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";

const Home = () => {
  const [promotions, setPromotions] = useState([]);
  const [busca, setBusca] = useState("");

  useEffect(() => {
    const params = {};

    if (busca) {
      params.id = busca;
    }

    loadUsers();
  }, [busca]);

  const loadUsers = async () => {
    const result = await axios.get("http://localhost:3333/api/produtos");
    setPromotions(result.data);
  };

  const deleteUser = async (_id) => {
    await axios.delete(`http://localhost:3333/api/delete/produtos/${_id}`);
    loadUsers();
  };

  return (
    <div className='container'>
      <div className='py-4'>
        <Link to='/PageForm'>
          <h1>Novo usuario</h1>
        </Link>
        <input
          type='search'
          placeholder='buscar'
          value={busca}
          onChange={(ev) => setBusca(ev.target.value)}
        />

        <Card>
          <TableContainer component={Paper}>
            <Table aria-label='simple table'>
              <TableHead>
                <TableRow>
                  <TableCell align='center'>Calories</TableCell>
                  <TableCell align='center'>Fat&nbsp;(g)</TableCell>
                  <TableCell align='center'>Carbs&nbsp;(g)</TableCell>
                  <TableCell align='center'>Protein&nbsp;(g)</TableCell>
                  <TableCell align='center'>
                    <button>clicar</button>
                  </TableCell>
                </TableRow>
              </TableHead>
            </Table>
          </TableContainer>
        </Card>

        <table class='table border shadow'>
          <tbody>
            {promotions.map((user) => (
              <tr key={user._id}>
                <td>{user.nome_produto}</td>
                <td>{user.descricao}</td>
                <td>{user.quantidade_produto}</td>
                <td>
                  <Link class='btn btn-primary mr-2' to={`/users/${user._id}`}>
                    View
                  </Link>

                  <Link
                    class='btn btn-outline-primary mr-2'
                    to={`/users/edit/${user._id}`}
                  >
                    Edit
                  </Link>

                  <Link
                    class='btn btn-danger'
                    onClick={() => deleteUser(user._id)}
                  >
                    Delete
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Home;

